import React, { useRef, useEffect, useState } from 'react';

interface AudioManagerProps {
  isLetterOpened: boolean;
}

const AudioManager: React.FC<AudioManagerProps> = ({ isLetterOpened }) => {
  const voicyAudioRef = useRef<HTMLAudioElement>(null);
  const musicAudioRef = useRef<HTMLAudioElement>(null);
  const [voicyFiles] = useState([
    'Voicy_1.mp3',
    'Voicy_2.mp3',
    'Voicy_3.mp3',
    'Voicy_4.mp3',
    'Voicy_5.mp3'
  ]);

  useEffect(() => {
    if (isLetterOpened) {
      playRandomVoicyClip();
    }
  }, [isLetterOpened]);

  const playRandomVoicyClip = () => {
    if (voicyAudioRef.current) {
      // Get a random Voicy file
      const randomFile = voicyFiles[Math.floor(Math.random() * voicyFiles.length)];
      voicyAudioRef.current.src = `/audio/voicy/${randomFile}`;
      voicyAudioRef.current.volume = 0.7;
      voicyAudioRef.current.play().catch(console.error);
    }
  };

  const playHedwigsTheme = () => {
    if (musicAudioRef.current) {
      musicAudioRef.current.src = '/audio/music/Hedwigs Theme.mp3';
      musicAudioRef.current.volume = 0.3; // Soft background music
      musicAudioRef.current.loop = true;
      musicAudioRef.current.play().catch(console.error);
    }
  };

  const handleVoicyEnded = () => {
    // After Voicy clip ends, start Hedwig's Theme
    setTimeout(() => {
      playHedwigsTheme();
    }, 500); // Small delay for smooth transition
  };

  return (
    <>
      {/* Voicy Audio Element */}
      <audio
        ref={voicyAudioRef}
        onEnded={handleVoicyEnded}
        preload="metadata"
      />

      {/* Background Music Audio Element */}
      <audio
        ref={musicAudioRef}
        preload="metadata"
      />
    </>
  );
};

export default AudioManager;
